mapper.select:
SELECT COUNT(*) FROM RECIPE_POST


        AND (TITLE LIKE '%' || #{keyword} || '%' OR CONTENTS LIKE '%' || #{keyword} || '%')


        AND POST_CATEGORY = #{boardType}

mapper.insert:
INSERT INTO REPLY (
    QS_NO, USERID, REPLY_CONTENTS, ADMIN_ID
) VALUES (
    #{qsNo}, #{userId}, #{replyContents}, #{adminId}
)

mapper.update:
UPDATE REPLY SET
    REPLY_CONTENTS = #{replyContents},
    ADMIN_ID = #{adminId}
WHERE REPLY_NO = #{replyNo}

mapper.select:
SELECT QS_NO as qsNo, USERID as userId, QS_TITLE as qsTitle, QS_CONTENTS as qsContents, QS_STATUS as qsStatus, CDATETIME as cdatetime, VIEW_CNT as viewCnt, QS_CATEGORY as qsCategory FROM QUESTION
QS_STATUS = #{status}
ORDER BY QS_NO DESC

mapper.update:
UPDATE QUESTION SET 
    QS_STATUS = #{status}
WHERE QS_NO = #{qsNo}

UPDATE DELIVERY SET
    DELIVERY_STATUS = #{status}
WHERE DELIVERY_NO = #{deliveryNo}

UPDATE DELIVERY SET
    TRACKING_NUMBER = #{trackingNumber}
WHERE DELIVERY_NO = #{deliveryNo}

UPDATE PRODUCT
SET STATUS = #{status}
WHERE ITEM_NO = #{itemNo}

mapper.insert:
INSERT INTO ITEM_QUESTION_REPLY (
    IQ_NO, USERID, REPLY_CONTENTS, ADMIN_ID
) VALUES (
    #{qsNo}, #{userId}, #{replyContents}, #{adminId}
)

mapper.update:
UPDATE ITEM_QUESTION SET 
    IQ_STATUS = #{status}
WHERE IQ_NO = #{qsNo}

mapper.select:
SELECT *
FROM reply
WHERE qs_no = #{qsNo}
