mapper.update:
UPDATE MEMBER
SET STATUS = #{status}
WHERE USERID = #{memberId}

mapper.select:
SELECT * FROM ( SELECT w.WISH_KEY as wishKey, w.ITEM_NO as itemNo, p.ITEM_NAME as recentWishProduct, p.PRICE as price, TO_CHAR(w.ADD_DATE, 'YYYY.MM.DD') as addDate, ROW_NUMBER() OVER (
ORDER BY w.ADD_DATE DESC ORDER BY w.ADD_DATE ASC ORDER BY p.PRICE DESC ORDER BY p.PRICE ASC ORDER BY w.ADD_DATE DESC
) AS rn FROM WISH w JOIN PRODUCT p ON w.ITEM_NO = p.ITEM_NO WHERE w.USERID = #{userId} ) WHERE rn BETWEEN #{offset} + 1 AND #{offset} + #{pageSize}

mapper.update:
UPDATE MEMBER
NICKNAME = #{nickname}, PHONE = #{phone}, EMAIL = #{email}, ADDRESS = #{address}, PASSWORD = #{password}, UDATETIME = SYSDATE
WHERE USERID = #{userId}

mapper.select:
SELECT password
FROM member
WHERE userid = #{userId}

SELECT COUNT(*)
FROM order_list
WHERE userid = #{userId}
