mapper.select:
SELECT COUNT(*) FROM PRODUCT P LEFT JOIN PRODUCT_IMG I ON P.ITEM_NO = I.ITEM_NO WHERE THUMBNAIL = 'Y'
AND ITEM_NAME LIKE '%' || #{keyword} || '%'
AND CATEGORY = #{searchOption}

SELECT ITEM_COUNT
FROM PRODUCT
WHERE ITEM_NO = #{itemNo}

mapper.update:
UPDATE PRODUCT
SET ITEM_CONTENTS = #{contentImagePath}
WHERE ITEM_NO = #{itemNo}

UPDATE PRODUCT
SET ITEM_CONTENTS = #{contentImagePath}
WHERE ITEM_NO = #{itemNo}


UPDATE PRODUCT_IMG  
SET FILENAME=#{filename}, FILEPATH=#{path}  
WHERE ITEM_NO=#{itemNo} AND THUMBNAIL='Y'

mapper.select:
SELECT COUNT(*)
FROM WISH
WHERE USERID = #{userId} AND ITEM_NO = #{itemNo}

SELECT COUNT(*)
FROM REVIEW
WHERE ITEM_NO = #{itemNo}

SELECT COUNT(*)
FROM ITEM_QUESTION
WHERE ITEM_NO = #{itemNo}
